-- Create "signal" table
CREATE TABLE "signal" ("name" character varying(100) NOT NULL, "next_signal_name" character varying(100) NULL, "track_circuit_id" integer NULL, "lever_id" integer NULL, PRIMARY KEY ("name"), CONSTRAINT "signal_lever_id_fkey" FOREIGN KEY ("lever_id") REFERENCES "lever" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "signal_next_signal_name_fkey" FOREIGN KEY ("next_signal_name") REFERENCES "signal" ("name") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "signal_track_circuit_id_fkey" FOREIGN KEY ("track_circuit_id") REFERENCES "track_circuit" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
