-- Create "track_circuit_signal" table
CREATE TABLE "track_circuit_signal" ("id" bigserial NOT NULL, "track_circuit_id" bigint NOT NULL, "is_up" boolean NOT NULL, "signal_name" character varying(100) NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "track_circuit_signal_track_circuit_id_is_up_signal_name_key" UNIQUE ("track_circuit_id", "is_up", "signal_name"), CONSTRAINT "track_circuit_signal_signal_name_fkey" FOREIGN KEY ("signal_name") REFERENCES "signal" ("name") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "track_circuit_signal_track_circuit_id_fkey" FOREIGN KEY ("track_circuit_id") REFERENCES "track_circuit" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
